create table if not exists room
(
    room_id   bigserial primary key,
    capacity  integer not null,
    room_type varchar(255),
    constraint room_room_status_check check (room_status in ('VACANT', 'OCCUPIED', 'RESERVED', 'MAINTENANCE')),
